	// /
	// / 获得一个字符串的汉语拼音码
	// /
	// / name="strText">字符串
	// / 汉语拼音码,该字符串只包含大写的英文字母
	public static String GetAllFirstPinyin(String strText) {
		if (strText == null || strText.length() == 0)
			return strText;
		strText = strText.replaceAll(" ", "").replaceAll("　", "");
		StringBuilder myStr = new StringBuilder();
		for (int i = 0; i < strText.length(); i++) {
			char vChar = strText.charAt(i);
			// 若是字母则直接输出
			if ((vChar >= 'a' && vChar <= 'z') || (vChar >= 'A' && vChar <= 'Z'))
				myStr.append(vChar);
			else if ((vChar >= 'ａ' && vChar <= 'ｚ') || (vChar >= 'Ａ' && vChar <= 'Ｚ')) {
				int d = 'Ａ' - 'a';
				vChar = (char) ((int) vChar - d);
				myStr.append(vChar);

			} else if ((int) vChar >= 19968 && (int) vChar <= 40869) {
				// 对可以查找的汉字计算它的首拼音字母的位置，然后输出
				myStr.append(strChineseFirstPY.charAt((int) vChar - 19968));
			} else {
				myStr.append(vChar);
			}
		}
		return myStr.toString().toLowerCase();
	}
	
	public static String GetFirstPinyin(String strText) {
		
		if (strText == null || strText.length() == 0)
			return strText;
		String myStr = null;
		char vChar = strText.charAt(0);
		
		// 若是字母则直接返回
		if ((vChar >= 'a' && vChar <= 'z') || (vChar >= 'A' && vChar <= 'Z')) {
			myStr = String.valueOf(vChar);
		} else if ((vChar >= 'ａ' && vChar <= 'ｚ') || (vChar >= 'Ａ' && vChar <= 'Ｚ')) {
			int d = 'Ａ' - 'a';
			vChar = (char) ((int) vChar - d);
			myStr = String.valueOf(vChar);

		} else if ((int) vChar >= 19968 && (int) vChar <= 40869) {
			// 对可以查找的汉字计算它的首拼音字母的位置，然后输出
			myStr = String.valueOf(strChineseFirstPY.charAt((int) vChar - 19968));
		} else {
			return String.valueOf(vChar);
		}

		return myStr.toLowerCase();
	}

	// 300 公用
	// 300 成长 300cz
	public static String[] GetAllMultiFirstPinyin(String strText) {
		if (strText == null) {
			return new String[] { "" };
		}
		int multiIndex = -1;
		char multiChar = 0;
		// for (int i = 0; i < strText.length(); i++) {
		// char ch = GetMulti(strText.charAt(i));
		// 我改动的代码
		String t_s = strText.replaceAll(" ", "").replaceAll("　", "");
		for (int i = 0; i < t_s.length(); i++) {
			char ch = GetMulti(t_s.charAt(i));
			if (ch != 0) {
				multiIndex = i;
				multiChar = ch;
				break;
			}
		}
		String result = GetAllFirstPinyin(strText);
		if (multiIndex == -1) {
			return new String[] { result };
		} else {
			return new String[] { result, result.substring(0, multiIndex) + multiChar + result.substring(multiIndex + 1) };
		}
	}

	public static char GetMulti(char ch) {
		for (int i = 0; i < charMulti.length; i++) {
			if (charMulti[i] == ch) {
				return firstCharMulti[i];
			}
		}
		return 0;
	}

	public static void main(String[] args) {
		String[] results = GetAllMultiFirstPinyin("洛阳钼业　　　　");
		for (int i = 0; i < results.length; i++) {
			System.out.println(results[i]);
		}
	}
